import React from 'react'
import { useStateValue } from "./StateProvider"

function Product({ id, title, image, price, description }) {

    const [{ }, dispatch] = useStateValue();

    const addToBasket = () => {
        dispatch({
            type: 'ADD_TO_BASKET',
            item: {
                id: id,
                title: title,
                image: image,
                price: price,
            }
        })
    };

    return (
        <>

            <div class="card mb-3" style={{ maxWidth: "550px;" }}>
                <div class="row g-0">
                    <div class="col-md-4">
                        <img src={image} class="img-fluid rounded-start" alt="..." />
                    </div>
                    <div class="col-md-8 px-3 mb-4">
                        <div class="card-body">
                            <h5 class="card-title">{title}</h5>
                            <p class="card-text text-muted"><small>{description}</small></p>
                            <p class="card-text"><mark><small>&#8377; </small><strong>{price}</strong></mark></p>
                        </div>
                        <button className='btn btn-sm bg-light border' type='button' onClick={addToBasket}>Add to Basket</button>
                    </div>
                </div>
            </div>

        </>
    )
}

export default Product;
